pragma solidity ^0.4.17;

contract EnergyMarket {

    uint constant HI_RATE_PRICE = 2;
    uint constant LOW_RATE_PRICE = 1;

    struct EnergyAuction {
        uint quantity; // amount of energy generated
        uint createdAt; // time of energy creation
        address createdBy; // who the energy was created by
        uint basePrice; // min price charged for the energy
        uint auctionEnd; // time of auction end
        uint highestBid; // value of highest bid made
        address highestBidder; // address of highest bidder
        bool ended; // has the auction ended?
    }

    mapping(uint => EnergyAuction) auctions;
    mapping(address => bool) public hasRegistered;
    mapping(address => uint) public coinBalances;
    mapping(address => uint) public energyBalances;

    bool isAvailableEnergy = false;
    uint numAuctions = 0;
    uint firstValidAuction = 0;
    uint totalAvailableEnergy = 0;

    event EnergyGenerated(uint auctionId, uint quantity, uint auctionEnd, address createdBy);
    event BidIncreased(uint auctionId, address bidder, uint amount);
    event AuctionEnded(uint auctionId, address highestBidder, uint highestBid);
    event EnergyConsumed(uint auctionId, address createdBy);

    modifier validAuction(uint auctionId) {
        require(auctionId >= firstValidAuction);
        _;
    }

    modifier onlyRegistered() {
        require(hasRegistered[msg.sender]);
        _;
    }


    function registerUser() public {
        // TODO: attack vulnerability because will create extra memory; require ether to be sent with payable
        require(!hasRegistered[msg.sender]);
        hasRegistered[msg.sender] = true;
        coinBalances[msg.sender] = 1000;
    }

    function generateEnergy(uint _quantity, uint _biddingTime) public onlyRegistered {
        require(_quantity > 0);

        energyBalances[msg.sender] += _quantity;
        totalAvailableEnergy += _quantity;

        uint auctionId = numAuctions++;
        auctions[auctionId] = EnergyAuction({
            quantity: _quantity,
            createdAt: now,
            createdBy: msg.sender,
            basePrice: lookupPrice(now),
            auctionEnd: now + _biddingTime,
            highestBid: 0,
            highestBidder: 0,
            ended: false
        });

        isAvailableEnergy = true;
        emit EnergyGenerated(auctionId, _quantity, now + _biddingTime, msg.sender);
    }

    function bidForEnergy(uint auctionId, uint bid) public onlyRegistered {
        require(coinBalances[msg.sender] >= bid);

        EnergyAuction storage a = auctions[auctionId];

        require(now <= a.auctionEnd);
        // require(bid > a.highestBid);
        if (bid <= a.highestBid) {
            return;
        }

        if (a.highestBidder != 0) {
            coinBalances[a.highestBidder] += a.highestBid;
        }

        a.highestBidder = msg.sender;
        a.highestBid = bid;
        coinBalances[msg.sender] -= bid;
        emit BidIncreased(auctionId, msg.sender, bid); //TODO: add auction id
    }

    function endAuction(uint auctionId) public onlyRegistered {
        EnergyAuction storage a = auctions[auctionId];

        require(msg.sender == a.createdBy);

        require(!a.ended);
        require(now >= a.auctionEnd);

        a.ended = true;

        // TODO: figure out what I want to do with firstValidAuction
        firstValidAuction++;
        if (firstValidAuction == numAuctions) {
            isAvailableEnergy = false;
        }

        if (a.highestBidder == 0) {
            energyBalances[a.createdBy] -= a.quantity;
            totalAvailableEnergy -= a.quantity;
            coinBalances[a.createdBy] += a.basePrice * a.quantity;
            emit EnergyConsumed(auctionId, a.createdBy);
        }

        emit AuctionEnded(auctionId, a.highestBidder, a.highestBid);
    }

    function buyerApprove(uint auctionId) public onlyRegistered {
        EnergyAuction storage a = auctions[auctionId];

        require(a.ended);
        require(msg.sender == a.highestBidder);

        energyBalances[a.createdBy] -= a.quantity;
        totalAvailableEnergy -= a.quantity;
        coinBalances[a.createdBy] += a.highestBid;

        emit EnergyConsumed(auctionId, a.createdBy);
        clean(auctionId);
    }

    function buyerAbort(uint auctionId) public onlyRegistered {
        EnergyAuction storage a = auctions[auctionId];

        require(a.ended);
        require(msg.sender == a.highestBidder);

        coinBalances[msg.sender] += a.highestBid;
        clean(auctionId);
    }

    function clean(uint auctionId) private {
        EnergyAuction storage a = auctions[auctionId];
        a.highestBid = 0;
        a.highestBidder = 0;
        a.auctionEnd = 0;
        a.createdBy = 0;
        a.createdAt = 0;
        a.quantity = 0;
        a.basePrice = 0;
        a.ended = false;
    }

