    function getAvailableEnergy() public view returns (uint) {
        return totalAvailableEnergy;
    }

    function isRegistered(address usr) public view returns (bool) {
        return hasRegistered[usr];
    }

    function getLatestAuction() public view returns (uint, address, uint, uint) {
        require(isAvailableEnergy);
        EnergyAuction storage a = auctions[numAuctions-1];
        return (numAuctions-1, a.createdBy, a.quantity, a.auctionEnd);
    }

    function getEnergyBalance(address usr) public view returns (uint) {
        require(hasRegistered[usr]);
        return energyBalances[usr];
    }

    function getCoinBalance(address usr) public view returns (uint) {
        require(hasRegistered[usr]);
        return coinBalances[usr];
    }

    function getHighestBid(uint auctionId) public view returns (uint) {
        EnergyAuction storage a = auctions[auctionId];
        return a.highestBid;
    }

    function lookupPrice(uint time) private pure returns (uint) {
        // TODO: find out a way to determine the time of the day based on the absolute unix timestamp
        if (time > 0) {
            return LOW_RATE_PRICE;
        } else {
            return HI_RATE_PRICE;
        }
    }
}
